exports.update_operators = {
  $set: function (obj1, obj2) {
    var final_obj = {};
    
    // merge multiple objects, ignoring prototypes
    [obj1, obj2].forEach(function (obj) {
      Object.keys(obj).forEach(function (key) {
        final_obj[key] = obj[key];
      });
    });

    return final_obj;
  },
  $unset: function (obj1, obj2) {
    Object.keys(obj2).forEach(function (key) {
      delete obj1[key];
    });

    return obj1;
  }
};
