var common = require('../common');
var orchestrate = require('orchestrate');

var USAGE = "orcli get [collection] (key)";

module.exports = function (collection, key, kwargs) {
  var db = common.get_db(this.api_key);

  if (!collection) throw new Error(USAGE);

  if (kwargs) {
    return db.get(collection, key);
  } else {
    return db.list(collection, kwargs);
  }
};
