var get = require('../../lib-cov/commands/get');
var nock = require('nock');
var OIO_URL = 'https://api.orchestrate.io';
var assert = require('assert');

describe('event', function () {
  afterEach(function () {
    nock.cleanAll();
  });

  it('should get an item', function (done) {
    var scope = nock(OIO_URL)
    .get('/v0/users/garbados')
    .reply(200);

    get('users', 'garbados', {})
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });

  it('should get the first page of a collection', function (done) {

    var scope = nock(OIO_URL)
    .get('/v0/users')
    .reply(200);

    get('users', {})
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });
});
