var merge = require('../../lib-cov/commands/merge');
var nock = require('nock');
var OIO_URL = 'https://api.orchestrate.io';
var assert = require('assert');

describe('merge', function () {
  afterEach(function () {
    nock.cleanAll();
  });

  it('should merge updates', function (done) {
    var scope = nock(OIO_URL)
    .patch('/v0/users/garbados')
    .reply(201);

    merge(['users', 'garbados'], {
      is_a_babe: true
    })
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });

  it('should merge updates conditionally', function (done) {
    var scope = nock(OIO_URL)
    .patch('/v0/users/garbados')
    .reply(201);

    merge(['users', 'garbados'], {
      is_a_babe: true,
      "if-match": 'totes babe-like'
    })
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });
});
