var post = require('../../lib-cov/commands/post');
var nock = require('nock');
var OIO_URL = 'https://api.orchestrate.io';
var assert = require('assert');

describe('post', function () {
  afterEach(function () {
    nock.cleanAll();
  });

  it('should create an object', function (done) {
    var scope = nock(OIO_URL)
    .post('/v0/users')
    .reply(201);

    post(['users'], {
      user: 'garbados',
      is_a_babe: true
    })
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });
});
