var refs = require('../../lib-cov/commands/refs');
var nock = require('nock');
var OIO_URL = 'https://api.orchestrate.io';
var assert = require('assert');

describe('refs', function () {
  afterEach(function () {
    nock.cleanAll();
  });

  it('should list document versions', function (done) {
    var scope = nock(OIO_URL)
    .get('/v0/users/garbados/refs')
    .reply(200);

    refs(['users', 'garbados'], {})
    .then(function () {
      assert(scope.isDone());
      done();
    })
    .fail(done);
  });
});
